import React from 'react'


import { Spinner } from '../../components/Spinner'
import { useGetUsersQuery } from '../api/apiSlice'
import classnames from 'classnames'

let PostExcerpt = ({ post }) => {

  return (
    <article className="post-excerpt" key={post.id}>
      <div className="postss">
      <div className="item">ID: {post.id}</div>
      <div className="item">NAME: {post.name}</div>
      <div className="item">EMAIL: {post.email}</div>
      <div className="item">ADDRESS: {post.address.street} {post.address.suite} {post.address.city} {post.address.zipcode} {post.address.geo.flag} {post.address.geo.lng}</div>
      <div className="item">PHONE: {post.phone}</div>
      <div className="item">WEBSITE: {post.website}</div>
      <div className="item">COMPANY: {post.company.name}</div>  
      </div>
    </article>



  )
}

export const UsersList = () => {
  const {
    data: posts = [],
    isLoading,
    isFetching,
    isSuccess,
    isError,
    error,
   
  } = useGetUsersQuery()

  // const sortedPosts = useMemo(() => {
  //   const sortedPosts = posts.slice()
  //   // Sort posts in descending chronological order
  //   sortedPosts.sort((a, b) => b.date.localeCompare(a.date))
  //   return sortedPosts
  // }, [posts])

  let content

  if (isLoading) {
    content = <Spinner text="Loading..." />
  } else if (isSuccess) {
    const renderedPosts = posts.map(post => (
      <PostExcerpt key={post.id} post={post} />
    ))

    const containerClassname = classnames('posts-container', {
      disabled: isFetching
    })

    content = <div className={containerClassname}>{renderedPosts}</div>
  } else if (isError) {
    content = <div>{error.toString()}</div>
  }

  return (
    <section className="posts-list">
      <div className='contennt'>
        <p>USERS</p>
      </div>
      {content}
    </section>
  )
}
